<style lang="scss">
@use "../styles/config/breakpoints" as bp;

.Header {
    display: flex;
    width: 100%;
    max-width: calc(var(--content-width) - var(--gutter) * 2);
    margin: 0 auto calc(var(--gutter) * 2) auto;
    padding: calc(var(--gutter) * 2) var(--gutter);
    background: rgba(255, 255, 255, 0.2);
    border-bottom-right-radius: 1rem;
    border-bottom-left-radius: 1rem;
    -webkit-backdrop-filter: saturate(110%) blur(20px);
    backdrop-filter: saturate(110%) blur(20px);
    // TODO: add to theme
    box-shadow: 20px 20px 60px #2898d9, -20px -20px 60px #36ceff;

    &__logo {
        display: flex;
        align-items: center;
        flex-wrap: nowrap;
        margin: 0 auto;
        color: white;

        &-symbol {
            > svg {
                width: 30px;

                @media screen and (min-width: #{bp.$sm}) {
                    width: 60px;
                }
            }
        }

        &-text {
            display: inline-block;
            margin-left: var(--gutter);

            @media screen and (min-width: #{bp.$sm}) {
                margin-left: calc(var(--gutter) * 2);
            }

            > svg {
                height: 20px;

                @media screen and (min-width: #{bp.$sm}) {
                    height: 40px;
                }
            }
        }

        svg {
            fill: currentColor;
        }
    }
}
</style>
